function iniciarApp(){mostrarConfirmacion()}function mostrarConfirmacion(){document.querySelectorAll(".formulario-eliminar").forEach(n=>{n.addEventListener("submit",n=>{n.preventDefault(),modalConfirmacion(n.target,"¿Estas seguro que quieres eliminar un usuario?")})})}function modalConfirmacion(n,o){Swal.fire({title:o,text:"¡No podrás revertir esto!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"¡Si, Elimínalo!",cancelButtonText:"Cancelar",customClass:{popup:"modal",icon:"modal-icon",title:"modal-title",htmlContainer:"modal-warning",actions:"modal-btns",confirmButton:"modal-btn-confirm",cancelButton:"modal-btn-cancel"}}).then(o=>{o.isConfirmed&&n.submit()})}document.addEventListener("DOMContentLoaded",iniciarApp);
